import React, { useState } from "react";
import "./Alarm.css"; // Adjust the path as per your file structure

const Alarm = ({ alarm, onSave, onCancel, onDelete }) => {
  const [localAlarm, setLocalAlarm] = useState({ ...alarm });

  const handleSave = () => {
    onSave(localAlarm);
  };

  const handleCancel = () => {
    onCancel();
  };

  const handleDelete = () => {
    onDelete();
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setLocalAlarm({ ...localAlarm, [name]: value });
  };

  return (
    <div className="alarm-container">
      <h2>Alarm Settings</h2>
      <input
        type="text"
        name="title"
        placeholder="Alarm Title"
        value={localAlarm.title}
        onChange={handleChange}
      />
      <input
        type="time"
        name="time"
        value={localAlarm.time}
        onChange={handleChange}
      />
      <div>
        <button onClick={handleSave}>Save</button>
        <button onClick={handleCancel}>Cancel</button>
        <button onClick={handleDelete}>Delete</button>
      </div>
    </div>
  );
};

export default Alarm;
