import React, { useState } from "react";
import "./App.css";
import "./Theme.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faClock,
  faHourglassHalf,
  faBell,
  faGlobe,
  faStopwatch,
  faPalette,
} from "@fortawesome/free-solid-svg-icons";

const App = () => {
  const [view, setView] = useState("clock"); // State to manage current view

  // Function to render the selected view based on `view` state
  const renderView = () => {
    switch (view) {
      case "clock":
        return <Clock />;
      case "timer":
        return <Timer />;
      case "alarm":
        return <Alarm />;
      case "worldClock":
        return <WorldClock />;
      case "stopwatch":
        return <Stopwatch />;
      case "themeSelector":
        return <ThemeSelector />;
      default:
        return <Clock />; // Default view
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>UltraModern Clock</h1>
      </header>

      {/* Content area */}
      <div className="App-content">{renderView()}</div>

      {/* Icon buttons fixed at the bottom */}
      <div className="icon-bar">
        <button onClick={() => setView("clock")}>
          <FontAwesomeIcon icon={faClock} /> Clock
        </button>
        <button onClick={() => setView("timer")}>
          <FontAwesomeIcon icon={faHourglassHalf} /> Timer
        </button>
        <button onClick={() => setView("alarm")}>
          <FontAwesomeIcon icon={faBell} /> Alarm
        </button>
        <button onClick={() => setView("worldClock")}>
          <FontAwesomeIcon icon={faGlobe} /> World Clock
        </button>
        <button onClick={() => setView("stopwatch")}>
          <FontAwesomeIcon icon={faStopwatch} /> Stopwatch
        </button>
        <button onClick={() => setView("themeSelector")}>
          <FontAwesomeIcon icon={faPalette} /> Themes
        </button>
      </div>
    </div>
  );
};

export default App;
