import React, { useState, useEffect } from "react";
import moment from "moment-timezone";
import Select from "react-select";
import "./WorldClock.css";

const WorldClock = () => {
  const countryOptions = [
    { value: "America/New_York", label: "New York, USA" },
    { value: "Europe/London", label: "London, UK" },
    { value: "Asia/Tokyo", label: "Tokyo, Japan" },
    { value: "Europe/Athens", label: "Athens, Greece" },
    { value: "Africa/Accra", label: "Accra, Ghana" },
    { value: "Africa/Lagos", label: "Lagos, Nigeria" },
    { value: "Africa/Kigali", label: "Kigali, Rwanda" },
    { value: "Africa/Johannesburg", label: "Johannesburg, South Africa" },
    { value: "Africa/Dakar", label: "Dakar, Senegal" },
    { value: "Africa/Bamako", label: "Bamako, Mali" },
    { value: "Europe/Zurich", label: "Zurich, Switzerland" },
  ];

  const [selectedCountry, setSelectedCountry] = useState(countryOptions[0]);
  const [time, setTime] = useState(
    moment().tz(selectedCountry.value).format("HH:mm:ss")
  );
  const [searchQuery, setSearchQuery] = useState("");

  useEffect(() => {
    const updateTime = () => {
      setTime(moment().tz(selectedCountry.value).format("HH:mm:ss"));
    };

    updateTime();
    const interval = setInterval(updateTime, 1000);
    return () => clearInterval(interval);
  }, [selectedCountry]);

  const filteredOptions = countryOptions.filter((option) =>
    option.label.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="world-clock">
      <input
        type="text"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        placeholder="Search for a country"
        className="search-bar"
      />
      <Select
        options={filteredOptions}
        value={selectedCountry}
        onChange={setSelectedCountry}
        className="country-select"
      />
      <div className="country-time">
        <h3>{selectedCountry.label}</h3>
        <p>{time}</p>
      </div>
    </div>
  );
};

export default WorldClock;
