import React, { useState, useEffect } from "react";
import moment from "moment-timezone";
import "./WorldClock.css";

const WorldClock = () => {
  const countryTimezones = {
    "New York, USA": "America/New_York",
    "London, UK": "Europe/London",
    "Tokyo, Japan": "Asia/Tokyo",
    "Athens, Greece": "Europe/Athens",
    "Accra, Ghana": "Africa/Accra",
    "Lagos, Nigeria": "Africa/Lagos",
    "Kigali, Rwanda": "Africa/Kigali",
    "Johannesburg, South Africa": "Africa/Johannesburg",
    "Dakar, Senegal": "Africa/Dakar",
    "Bamako, Mali": "Africa/Bamako",
    "Zurich, Switzerland": "Europe/Zurich",
  };

  const [times, setTimes] = useState({});

  useEffect(() => {
    const updateTimes = () => {
      const newTimes = {};
      Object.keys(countryTimezones).forEach((country) => {
        newTimes[country] = moment()
          .tz(countryTimezones[country])
          .format("HH:mm:ss");
      });
      setTimes(newTimes);
    };

    updateTimes();
    const interval = setInterval(updateTimes, 1000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="world-clock">
      {Object.keys(times).map((country) => (
        <div key={country} className="country-time">
          <h3>{country}</h3>
          <p>{times[country]}</p>
        </div>
      ))}
    </div>
  );
};

export default WorldClock;
