import React, { useState } from "react";
import "./App.css";
import "./Theme.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faClock,
  faHourglassHalf,
  faBell,
  faGlobe,
  faStopwatch,
  faPalette,
} from "@fortawesome/free-solid-svg-icons";

// Import your clock components here
import Clock from "./Components/Clock";
import Timer from "./Components/Timer";
import Alarm from "./Components/Alarm";
import WorldClock from "./Components/WorldClock";
import Stopwatch from "./Components/Stopwatch";
import ThemeSelector from "./Components/ThemeSelector";

const App = () => {
  const [theme, setTheme] = useState("default");
  const [view, setView] = useState("clock"); // Initialize view state with default view
  const [showThemes, setShowThemes] = useState(false); // State to toggle theme buttons visibility

  const applyTheme = (selectedTheme) => {
    setTheme(selectedTheme);
    document.body.className = selectedTheme; // Apply the selected theme class to the body
    setShowThemes(false); // Hide theme buttons after selecting a theme
  };

  const renderView = () => {
    switch (view) {
      case "clock":
        return <Clock />;
      case "timer":
        return <Timer />;
      case "alarm":
        return <Alarm />;
      case "worldClock":
        return <WorldClock />;
      case "stopwatch":
        return <Stopwatch />;
      case "themeSelector":
        return <ThemeSelector setTheme={setTheme} />;
      default:
        return <Clock />;
    }
  };

  return (
    <div className={`App ${theme}`}>
      <header className="App-header">
        <h1>UltraModern Clock</h1>
        <button onClick={() => setView("clock")}>
          <FontAwesomeIcon icon={faClock} /> Clock
        </button>
        <button onClick={() => setView("timer")}>
          <FontAwesomeIcon icon={faHourglassHalf} /> Timer
        </button>
        <button onClick={() => setView("alarm")}>
          <FontAwesomeIcon icon={faBell} /> Alarm
        </button>
        <button onClick={() => setView("worldClock")}>
          <FontAwesomeIcon icon={faGlobe} /> World Clock
        </button>
        <button onClick={() => setView("stopwatch")}>
          <FontAwesomeIcon icon={faStopwatch} /> Stopwatch
        </button>
        <button onClick={() => setShowThemes(!showThemes)}>
          <FontAwesomeIcon icon={faPalette} /> Themes
        </button>
      </header>

      {/* Render the selected view */}
      <div className="App-content">{renderView()}</div>

      {/* Theme buttons */}
      {showThemes && (
        <div className="theme-buttons">
          <button onClick={() => applyTheme("default")}>Default Theme</button>
          <button onClick={() => applyTheme("dark")}>Dark Theme</button>
          <button onClick={() => applyTheme("blue")}>Blue Theme</button>
          <button onClick={() => applyTheme("green")}>Green Theme</button>
          <button onClick={() => applyTheme("theme1")}>Theme 1</button>
          <button onClick={() => applyTheme("theme2")}>Theme 2</button>
        </div>
      )}
    </div>
  );
};

export default App;
