import React, { useState } from "react";
import "./Alarm.css";

const Alarm = ({ onSave, onCancel, onDelete }) => {
  const [title, setTitle] = useState("");
  const [time, setTime] = useState("");

  const handleSave = () => {
    const alarm = { title, time };
    onSave(alarm);
    setTitle("");
    setTime("");
  };

  return (
    <div className="alarm">
      <h2 className="alarm-title">Set Alarm</h2>
      <div className="alarm-inputs">
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Enter title"
          className="alarm-input"
        />
        <input
          type="time"
          value={time}
          onChange={(e) => setTime(e.target.value)}
          className="alarm-input"
        />
      </div>
      <div className="alarm-buttons">
        <button className="alarm-button" onClick={handleSave}>
          Save
        </button>
        <button className="alarm-button" onClick={onCancel}>
          Cancel
        </button>
        <button className="alarm-button" onClick={onDelete}>
          Delete
        </button>
      </div>
    </div>
  );
};

export default Alarm;
