import React from "react";
import "./ThemeSelector.css";

const themes = [
  "light-theme",
  "dark-theme",
  "neon-theme",
  "ocean-theme",
  "sunset-theme",
  "pastel-theme",
  "forest-theme",
  "night-sky-theme", // Add the new theme
];

const ThemeSelector = () => {
  const handleThemeChange = (theme) => {
    document.body.className = theme; // Apply the selected theme class to the body element

    if (theme === "night-sky-theme") {
      document.body.classList.add("starry-background");
    } else {
      document.body.classList.remove("starry-background");
    }
  };

  return (
    <div className="theme-selector">
      {themes.map((theme) => (
        <button
          key={theme}
          className="button"
          onClick={() => handleThemeChange(theme)}
        >
          {theme.replace("-", " ")}
        </button>
      ))}
    </div>
  );
};

export default ThemeSelector;
