import React, { useState, useEffect } from "react";
import "./Clock.css";

const Clock = () => {
  const [currentColor, setCurrentColor] = useState("#FFFFFF");
  const [time, setTime] = useState(new Date());

  // Update time every second
  useEffect(() => {
    const interval = setInterval(() => {
      setTime(new Date());
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  // Update color every second
  useEffect(() => {
    const interval = setInterval(() => {
      const randomColor =
        "#" + ((Math.random() * 0xffffff) << 0).toString(16).padStart(6, "0");
      setCurrentColor(randomColor);
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  const hours = time.getHours();
  const minutes = time.getMinutes();
  const seconds = time.getSeconds();
  const ampm = hours >= 12 ? "PM" : "AM";

  return (
    <div className="clock-container">
      <div className="time-display" style={{ color: currentColor }}>
        <div className="clock">{hours % 12 || 12}</div>
        <div className="flip-container">
          <div className="flip-card">
            <div className="flip-card-front">
              <span>{("0" + minutes).slice(-2)}</span>
            </div>
            <div className="flip-card-back">
              <span>{("0" + minutes).slice(-2)}</span>
            </div>
          </div>
        </div>
        <div className="flip-container">
          <div className="flip-card">
            <div className="flip-card-front">
              <span>{("0" + seconds).slice(-2)}</span>
            </div>
            <div className="flip-card-back">
              <span>{("0" + seconds).slice(-2)}</span>
            </div>
          </div>
        </div>
      </div>
      <div className="date-display">{time.toLocaleDateString()}</div>
      <div className="ampm-display">{ampm}</div>
    </div>
  );
};

export default Clock;
