import React, { useState, useEffect } from "react";
import "./App.css";
import Clock from "./Components/Clock";
import Timer from "./Components/Timer";
import Alarm from "./Components/Alarm";
import WorldClock from "./Components/WorldClock";
import Stopwatch from "./Components/Stopwatch";
import ThemeSelector from "./Components/ThemeSelector";

function App() {
  const [theme, setTheme] = useState("default");
  const [view, setView] = useState("clock");

  useEffect(() => {
    document.body.className = theme;
  }, [theme]);

  return (
    <div className="clock-container">
      <h1>
        <img
          src={`${process.env.PUBLIC_URL}/my-logo.png`}
          alt="UltraModern Clock Logo"
          className="logo"
        />
        UltraModern Clock
      </h1>
      {view === "clock" && <Clock />}
      {view === "timer" && <Timer />}
      {view === "alarm" && <Alarm />}
      {view === "worldClock" && <WorldClock />}
      {view === "stopwatch" && <Stopwatch />}

      <div className="button-container">
        <button onClick={() => setView("clock")} className="btn btn-primary">
          Clock
        </button>
        <button onClick={() => setView("timer")} className="btn btn-primary">
          Timer
        </button>
        <button onClick={() => setView("alarm")} className="btn btn-primary">
          Alarm
        </button>
        <button
          onClick={() => setView("worldClock")}
          className="btn btn-primary"
        >
          World Clock
        </button>
        <button
          onClick={() => setView("stopwatch")}
          className="btn btn-primary"
        >
          Stopwatch
        </button>
      </div>

      <ThemeSelector setTheme={setTheme} />
    </div>
  );
}

export default App;
