import React from "react";
import "./Alarm.css"; // Ensure this matches your actual CSS filename

const Alarm = ({ alarm, onSave, onCancel, onDelete }) => {
  if (!alarm) {
    return <div className="alarm">No alarm data provided.</div>;
  }

  const handleSave = () => {
    onSave(alarm);
  };

  const handleCancel = () => {
    onCancel(alarm);
  };

  const handleDelete = () => {
    onDelete(alarm);
  };

  return (
    <div className="alarm">
      <h2>{alarm.title}</h2>
      <p>{alarm.time}</p>

      <div className="alarm-buttons">
        <button className="alarm-button" onClick={handleSave}>
          Save
        </button>
        <button className="alarm-button" onClick={handleCancel}>
          Cancel
        </button>
        <button className="alarm-button" onClick={handleDelete}>
          Delete
        </button>
      </div>
    </div>
  );
};

export default Alarm;
