import React, { useState } from "react";
import "./App.css";
import "./Theme.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faClock,
  faHourglassHalf,
  faBell,
  faGlobe,
  faStopwatch,
  faPalette,
} from "@fortawesome/free-solid-svg-icons";
import Clock from "./Components/Clock";
import Timer from "./Components/Timer";
import Alarm from "./Components/Alarm";
import WorldClock from "./Components/WorldClock";
import Stopwatch from "./Components/Stopwatch";
import ThemeSelector from "./Components/ThemeSelector";

const App = () => {
  const [view, setView] = useState("clock");
  const [alarms, setAlarms] = useState([]);
  const [theme, setTheme] = useState("light");
  const [customImage, setCustomImage] = useState(null);

  const handleThemeChange = (newTheme) => {
    if (typeof newTheme === "object") {
      setTheme(newTheme.theme);
      setCustomImage(newTheme.image);
    } else {
      setTheme(newTheme);
      setCustomImage(null);
    }
  };

  const handleSaveAlarm = (updatedAlarm) => {
    const existingAlarmIndex = alarms.findIndex(
      (alarm) => alarm.id === updatedAlarm.id
    );
    if (existingAlarmIndex !== -1) {
      const updatedAlarms = [...alarms];
      updatedAlarms[existingAlarmIndex] = updatedAlarm;
      setAlarms(updatedAlarms);
    } else {
      setAlarms([...alarms, updatedAlarm]);
    }
  };

  const handleDeleteAlarm = (id) => {
    const updatedAlarms = alarms.filter((alarm) => alarm.id !== id);
    setAlarms(updatedAlarms);
  };

  const renderView = () => {
    switch (view) {
      case "clock":
        return <Clock />;
      case "timer":
        return <Timer />;
      case "alarm":
        return (
          <Alarm
            alarms={alarms}
            onSave={handleSaveAlarm}
            onDelete={handleDeleteAlarm}
          />
        );
      case "worldClock":
        return <WorldClock />;
      case "stopwatch":
        return <Stopwatch />;
      case "themeSelector":
        return <ThemeSelector onChangeTheme={handleThemeChange} />;
      default:
        return <Clock />;
    }
  };

  return (
    <div
      className={`App ${theme}-theme`}
      style={
        theme === "custom" && customImage
          ? { backgroundImage: `url(${customImage})`, backgroundSize: "cover" }
          : {}
      }
    >
      <header className="App-header">
        <h1>UltraModern Clock</h1>
      </header>
      <div className="App-content">{renderView()}</div>
      <div className="icon-bar">
        <button onClick={() => setView("clock")}>
          <FontAwesomeIcon icon={faClock} /> Clock
        </button>
        <button onClick={() => setView("timer")}>
          <FontAwesomeIcon icon={faHourglassHalf} /> Timer
        </button>
        <button onClick={() => setView("alarm")}>
          <FontAwesomeIcon icon={faBell} /> Alarm
        </button>
        <button onClick={() => setView("worldClock")}>
          <FontAwesomeIcon icon={faGlobe} /> World Clock
        </button>
        <button onClick={() => setView("stopwatch")}>
          <FontAwesomeIcon icon={faStopwatch} /> Stopwatch
        </button>
        <button onClick={() => setView("themeSelector")}>
          <FontAwesomeIcon icon={faPalette} /> Themes
        </button>
      </div>
    </div>
  );
};

export default App;
