import React, { useState, useEffect } from "react";
import "./Clock.css";

const Clock = () => {
  const [time, setTime] = useState(new Date());
  const [color, setColor] = useState("black");

  useEffect(() => {
    const intervalId = setInterval(() => {
      setTime(new Date());
      setColor(getRandomColor());
    }, 1000);

    return () => clearInterval(intervalId);
  }, []);

  const getRandomColor = () => {
    const colors = ["#FF5733", "#33FF57", "#3357FF", "#F333FF", "#FF33A6"];
    return colors[Math.floor(Math.random() * colors.length)];
  };

  const hours = String(time.getHours()).padStart(2, "0");
  const minutes = String(time.getMinutes()).padStart(2, "0");
  const seconds = String(time.getSeconds()).padStart(2, "0");
  const ampm = time.getHours() >= 12 ? "PM" : "AM";
  const date = time.toDateString();

  return (
    <div className="clock-container" style={{ color }}>
      <div className="date-display">{date}</div>
      <div className="flip-container">
        <div className="flip-card">{hours}</div>
        <div className="flip-card">{minutes}</div>
        <div className="flip-card">{seconds}</div>
      </div>
      <div className="ampm-display">{ampm}</div>
    </div>
  );
};

export default Clock;
