import React, { useState } from "react";
import "./Alarm.css";

const Alarm = ({ alarm, onSave, onCancel, onDelete }) => {
  const handleSave = () => {
    onSave(alarm);
  };

  const handleCancel = () => {
    onCancel();
  };

  const handleDelete = () => {
    onDelete();
  };

  return (
    <div className="alarm-container">
      <h2>Alarm Settings</h2>
      <input
        type="text"
        placeholder="Alarm Title"
        value={alarm?.title || ""}
        onChange={(e) => onSave({ ...alarm, title: e.target.value })}
      />
      <input
        type="time"
        value={alarm?.time || ""}
        onChange={(e) => onSave({ ...alarm, time: e.target.value })}
      />
      <div>
        <button onClick={handleSave}>Save</button>
        <button onClick={handleCancel}>Cancel</button>
        <button onClick={handleDelete}>Delete</button>
      </div>
    </div>
  );
};

export default Alarm;
