import React from "react";
import "./Alarm.css";

const Alarm = ({ alarms, onSave, onCancel, onDelete }) => {
  const handleSave = (alarm) => {
    onSave(alarm);
  };

  const handleCancel = (alarm) => {
    onCancel(alarm);
  };

  const handleDelete = (index) => {
    onDelete(index);
  };

  return (
    <div className="alarm-container">
      <h2>Alarms</h2>
      {alarms.length === 0 ? (
        <p>No alarms set.</p>
      ) : (
        alarms.map((alarm, index) => (
          <div key={index} className="alarm-item">
            <h3>{alarm.title}</h3>
            <p>{alarm.time}</p>
            <div className="alarm-buttons">
              <button className="save-button" onClick={() => handleSave(alarm)}>
                Save
              </button>
              <button
                className="cancel-button"
                onClick={() => handleCancel(alarm)}
              >
                Cancel
              </button>
              <button
                className="delete-button"
                onClick={() => handleDelete(index)}
              >
                Delete
              </button>
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default Alarm;
