import React from "react";

const ThemeSelector = ({ onChangeTheme }) => {
  const handleThemeChange = (event) => {
    onChangeTheme(event.target.value);
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = (e) => {
      onChangeTheme({ theme: "custom", image: e.target.result });
    };

    reader.readAsDataURL(file);
  };

  return (
    <div className="theme-selector">
      <h2>Select Theme</h2>
      <button value="light" onClick={handleThemeChange}>
        Light
      </button>
      <button value="dark" onClick={handleThemeChange}>
        Dark
      </button>
      <div>
        <label htmlFor="image-upload">Upload Image</label>
        <input id="image-upload" type="file" onChange={handleImageUpload} />
      </div>
    </div>
  );
};

export default ThemeSelector;
