import React, { useState } from "react";
import "./Alarm.css";

const Alarm = ({ alarms, onSave, onCancel, onDelete, onUpdate }) => {
  const [newAlarm, setNewAlarm] = useState({ title: "", time: "" });
  const [selectedTime, setSelectedTime] = useState("");

  const handleSave = () => {
    onSave(newAlarm);
    setNewAlarm({ title: "", time: "" }); // Clear input fields after saving
    setSelectedTime(""); // Clear selected time
  };

  const handleCancel = () => {
    onCancel();
    setSelectedTime(""); // Clear selected time
  };

  const handleDelete = (index) => {
    onDelete(index);
    setSelectedTime(""); // Clear selected time
  };

  const handleUpdate = (index) => {
    onUpdate(newAlarm, index);
    setNewAlarm({ title: "", time: "" }); // Clear input fields after updating
    setSelectedTime(""); // Clear selected time
  };

  const timeOptions = [
    "12:00 AM",
    "1:00 AM",
    "2:00 AM",
    "3:00 AM",
    "4:00 AM",
    "5:00 AM",
    "6:00 AM",
    "7:00 AM",
    "8:00 AM",
    "9:00 AM",
    "10:00 AM",
    "11:00 AM",
    "12:00 PM",
    "1:00 PM",
    "2:00 PM",
    "3:00 PM",
    "4:00 PM",
    "5:00 PM",
    "6:00 PM",
    "7:00 PM",
    "8:00 PM",
    "9:00 PM",
    "10:00 PM",
    "11:00 PM",
  ];

  return (
    <div className="alarm-container">
      <h2>Alarms</h2>
      {alarms.length === 0 ? (
        <p>No alarms set.</p>
      ) : (
        alarms.map((alarm, index) => (
          <div key={index} className="alarm-item">
            <h3>{alarm.title}</h3>
            <p>{alarm.time}</p>
            <div className="alarm-buttons">
              <button
                className="save-button"
                onClick={() => handleUpdate(index)}
              >
                Update
              </button>
              <button
                className="delete-button"
                onClick={() => handleDelete(index)}
              >
                Delete
              </button>
            </div>
          </div>
        ))
      )}
      <div className="alarm-input">
        <input
          type="text"
          placeholder="Enter title"
          value={newAlarm.title}
          onChange={(e) => setNewAlarm({ ...newAlarm, title: e.target.value })}
        />
        <div className="dropdown">
          <button className="dropbtn">Select</button>
          <div className="dropdown-content">
            {timeOptions.map((time, index) => (
              <a key={index} href="#" onClick={() => setSelectedTime(time)}>
                {time}
              </a>
            ))}
          </div>
        </div>
        <input
          type="text"
          placeholder="Selected time"
          value={selectedTime}
          readOnly
        />
        <button className="add-button" onClick={handleSave}>
          Add
        </button>
        <button className="cancel-button" onClick={handleCancel}>
          Cancel
        </button>
      </div>
    </div>
  );
};

export default Alarm;
