import React, { useState } from "react";
import "./Alarm.css";

const Alarm = ({ alarms, onSave, onDelete }) => {
  const [newAlarm, setNewAlarm] = useState({ title: "", time: "" });
  const [editMode, setEditMode] = useState(false);
  const [editIndex, setEditIndex] = useState(null);

  const handleSave = () => {
    if (editMode && editIndex !== null) {
      const updatedAlarms = [...alarms];
      updatedAlarms[editIndex] = newAlarm;
      onSave(updatedAlarms);
      setEditMode(false);
      setEditIndex(null);
    } else {
      onSave([...alarms, newAlarm]);
    }
    setNewAlarm({ title: "", time: "" });
  };

  const handleEdit = (index) => {
    setEditMode(true);
    setEditIndex(index);
    setNewAlarm(alarms[index]);
  };

  const handleDelete = (index) => {
    const updatedAlarms = alarms.filter((_, i) => i !== index);
    onDelete(updatedAlarms);
  };

  const handleCancel = () => {
    setEditMode(false);
    setEditIndex(null);
    setNewAlarm({ title: "", time: "" });
  };

  return (
    <div className="alarm-container">
      <h2>Alarms</h2>
      {alarms.length === 0 && <p>No alarms set.</p>}
      {alarms.map((alarm, index) => (
        <div className="alarm-item" key={index}>
          <h3>{alarm.title}</h3>
          <p>{alarm.time}</p>
          <div className="alarm-buttons">
            <button className="edit-button" onClick={() => handleEdit(index)}>
              Edit
            </button>
            <button
              className="delete-button"
              onClick={() => handleDelete(index)}
            >
              Delete
            </button>
          </div>
        </div>
      ))}
      {editMode && (
        <div className="alarm-item">
          <input
            type="text"
            placeholder="Title"
            value={newAlarm.title}
            onChange={(e) =>
              setNewAlarm({ ...newAlarm, title: e.target.value })
            }
          />
          <input
            type="time"
            value={newAlarm.time}
            onChange={(e) => setNewAlarm({ ...newAlarm, time: e.target.value })}
          />
          <div className="alarm-buttons">
            <button className="save-button" onClick={handleSave}>
              Save
            </button>
            <button className="cancel-button" onClick={handleCancel}>
              Cancel
            </button>
          </div>
        </div>
      )}
      {!editMode && (
        <button className="add-button" onClick={() => setEditMode(true)}>
          Add Alarm
        </button>
      )}
    </div>
  );
};

export default Alarm;
