import React, { useState } from "react";
import "./Alarm.css"; // Ensure this matches your actual CSS filename

const Alarm = ({ alarms, onSave, onCancel, onDelete }) => {
  const [newAlarm, setNewAlarm] = useState({
    title: "",
    time: "",
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setNewAlarm({ ...newAlarm, [name]: value });
  };

  const handleSave = () => {
    onSave(newAlarm);
    setNewAlarm({ title: "", time: "" }); // Clear input fields after saving
  };

  const handleCancel = () => {
    onCancel(); // Optionally handle cancel action
  };

  const handleDelete = (index) => {
    onDelete(index);
  };

  return (
    <div className="alarm-container">
      <h2>Alarms</h2>
      {alarms.length === 0 ? (
        <div>No alarms set.</div>
      ) : (
        alarms.map((alarm, index) => (
          <div key={index} className="alarm-item">
            <h3>{alarm.title}</h3>
            <p>{alarm.time}</p>
            <div className="alarm-buttons">
              <button className="save-button" onClick={() => handleSave()}>
                Save
              </button>
              <button className="cancel-button" onClick={() => handleCancel()}>
                Cancel
              </button>
              <button
                className="delete-button"
                onClick={() => handleDelete(index)}
              >
                Delete
              </button>
            </div>
          </div>
        ))
      )}
      <div>
        <input
          type="text"
          name="title"
          value={newAlarm.title}
          onChange={handleInputChange}
          placeholder="Enter title"
        />
        <input
          type="time"
          name="time"
          value={newAlarm.time}
          onChange={handleInputChange}
          placeholder="Select time"
        />
      </div>
    </div>
  );
};

export default Alarm;
