import React, { useState } from "react";
import Alarm from "./Alarm"; // Adjust the path as per your project structure

const App = () => {
  const [alarms, setAlarms] = useState([]);

  const handleSaveAlarm = (newAlarm) => {
    // Logic to save newAlarm to state
    setAlarms([...alarms, newAlarm]);
  };

  const handleCancelAlarm = (cancelledAlarm) => {
    // Logic to cancel or remove cancelledAlarm from state
    const updatedAlarms = alarms.filter(
      (alarm) => alarm.id !== cancelledAlarm.id
    );
    setAlarms(updatedAlarms);
  };

  const handleDeleteAlarm = (deletedAlarm) => {
    // Logic to delete deletedAlarm from state
    const updatedAlarms = alarms.filter(
      (alarm) => alarm.id !== deletedAlarm.id
    );
    setAlarms(updatedAlarms);
  };

  return (
    <div className="App">
      {/* Render your other components and content here */}
      <Alarm
        alarm={alarms.length > 0 ? alarms[0] : null} // Example: Pass the first alarm from state
        onSave={handleSaveAlarm}
        onCancel={handleCancelAlarm}
        onDelete={handleDeleteAlarm}
      />
    </div>
  );
};

export default App;
