import React, { useState } from "react";
import "./Alarm.css"; // Ensure this matches your actual CSS filename

const Alarm = ({ alarm, onSave, onCancel, onDelete }) => {
  // Assuming alarm details are managed by parent component

  const handleSave = () => {
    onSave(alarm); // Call onSave function passed from parent
  };

  const handleCancel = () => {
    onCancel(alarm); // Call onCancel function passed from parent
  };

  const handleDelete = () => {
    onDelete(alarm); // Call onDelete function passed from parent
  };

  return (
    <div className="alarm">
      {/* Alarm details display (e.g., title, time, etc.) */}
      <h2>{alarm.title}</h2>
      <p>{alarm.time}</p>

      {/* Alarm action buttons */}
      <div className="alarm-buttons">
        <button className="alarm-button" onClick={handleSave}>
          Save
        </button>
        <button className="alarm-button" onClick={handleCancel}>
          Cancel
        </button>
        <button className="alarm-button" onClick={handleDelete}>
          Delete
        </button>
      </div>
    </div>
  );
};

export default Alarm;
