import React, { useState } from "react";
import "./ThemeSelector.css";

const ThemeSelector = ({ onChangeTheme }) => {
  const [customImage, setCustomImage] = useState(null);

  const handleThemeChange = (theme) => {
    onChangeTheme(theme);
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = (e) => {
      setCustomImage(e.target.result);
      onChangeTheme({ theme: "custom", image: e.target.result });
    };

    reader.readAsDataURL(file);
  };

  return (
    <div className="theme-selector">
      <button onClick={() => handleThemeChange("light")}>Light Mode</button>
      <button onClick={() => handleThemeChange("dark")}>Dark Mode</button>
      <label className="upload-button">
        Upload Image
        <input type="file" onChange={handleImageUpload} />
      </label>
    </div>
  );
};

export default ThemeSelector;
