import React, { useState } from "react";
import "./Alarm.css";

const Alarm = ({ onSave, onCancel, onDelete, alarms }) => {
  const [title, setTitle] = useState("");
  const [time, setTime] = useState("");

  const handleSave = () => {
    const alarmData = { title, time };
    onSave(alarmData);
    setTitle("");
    setTime("");
  };

  const handleCancel = () => {
    onCancel();
    setTitle("");
    setTime("");
  };

  const handleDelete = (index) => {
    onDelete(index);
  };

  return (
    <div className="alarm-container">
      <h2>Set Alarm</h2>
      <div className="alarm-input">
        <label htmlFor="alarm-title">Alarm Title:</label>
        <input
          type="text"
          id="alarm-title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
      </div>
      <div className="alarm-input">
        <label htmlFor="alarm-time">Alarm Time:</label>
        <input
          type="time"
          id="alarm-time"
          value={time}
          onChange={(e) => setTime(e.target.value)}
        />
      </div>
      <div className="alarm-buttons">
        <button onClick={handleSave}>Save</button>
        <button onClick={handleCancel}>Cancel</button>
      </div>

      <h3>Saved Alarms</h3>
      <ul className="alarm-list">
        {alarms.map((alarm, index) => (
          <li key={index} className="alarm-item">
            <span>
              {alarm.title} - {alarm.time}
            </span>
            <button onClick={() => handleDelete(index)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Alarm;
