import React, { useState } from "react";
import "./Alarm.css";

const Alarm = ({ onSave, onCancel, onDelete }) => {
  const [title, setTitle] = useState("");
  const [time, setTime] = useState("");

  const handleSave = () => {
    const alarmData = { title, time };
    onSave(alarmData); // Invoke onSave prop with alarm data
  };

  const handleCancel = () => {
    onCancel(); // Invoke onCancel prop
  };

  const handleDelete = () => {
    onDelete(); // Invoke onDelete prop
  };

  return (
    <div className="alarm-container">
      <h2>Set Alarm</h2>
      <div className="alarm-input">
        <label htmlFor="alarm-title">Alarm Title:</label>
        <input
          type="text"
          id="alarm-title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
      </div>
      <div className="alarm-input">
        <label htmlFor="alarm-time">Alarm Time:</label>
        <input
          type="time"
          id="alarm-time"
          value={time}
          onChange={(e) => setTime(e.target.value)}
        />
      </div>
      <div className="alarm-buttons">
        <button onClick={handleSave}>Save</button>
        <button onClick={handleCancel}>Cancel</button>
        <button onClick={handleDelete}>Delete</button>
      </div>
    </div>
  );
};

export default Alarm;
