import React from "react";

const ThemeSelector = ({ onChangeTheme }) => {
  const handleThemeChange = (event) => {
    onChangeTheme(event.target.value);
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onload = (e) => {
      onChangeTheme({ theme: "custom", image: e.target.result });
    };

    reader.readAsDataURL(file);
  };

  return (
    <div className="theme-selector">
      <h2>Select Theme</h2>
      <button
        value="light"
        onClick={handleThemeChange}
        style={{
          backgroundColor: "#4CAF50",
          color: "#ffffff",
          padding: "10px 20px",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
          marginRight: "10px",
        }}
      >
        Light
      </button>
      <button
        value="dark"
        onClick={handleThemeChange}
        style={{
          backgroundColor: "#333",
          color: "#ffffff",
          padding: "10px 20px",
          border: "none",
          borderRadius: "5px",
          cursor: "pointer",
        }}
      >
        Dark
      </button>
      <div>
        <label
          htmlFor="image-upload"
          style={{
            fontSize: "18px",
            fontWeight: "bold",
            marginRight: "10px",
          }}
        >
          Upload Image
        </label>
        <input
          id="image-upload"
          type="file"
          onChange={handleImageUpload}
          style={{
            padding: "10px",
            fontSize: "18px",
            borderRadius: "5px",
            border: "1px solid #ccc",
          }}
        />
      </div>
    </div>
  );
};

export default ThemeSelector;
