import React, { useState, useEffect } from "react";
import "./Clock.css";

const Clock = () => {
  const [time, setTime] = useState(new Date());
  const [color, setColor] = useState("black");

  useEffect(() => {
    const interval = setInterval(() => {
      setTime(new Date());
      setColor(generateRandomColor());
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  const generateRandomColor = () => {
    const letters = "0123456789ABCDEF";
    let color = "#";
    for (let i = 0; i < 6; i++) {
      color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  };

  const hours = time.getHours();
  const minutes = time.getMinutes();
  const seconds = time.getSeconds();
  const ampm = hours >= 12 ? "PM" : "AM";
  const formattedHours = hours % 12 || 12;
  const formattedMinutes = minutes < 10 ? `0${minutes}` : minutes;
  const formattedSeconds = seconds < 10 ? `0${seconds}` : seconds;

  return (
    <div className="clock-container" style={{ color: color }}>
      <div className="date-display">{time.toDateString()}</div>
      <div className="time-display">{`${formattedHours}:${formattedMinutes}:${formattedSeconds}`}</div>
      <div className="ampm-display">{ampm}</div>
    </div>
  );
};

export default Clock;
