import React, { useState } from "react";

const Alarm = ({ alarm, onSave, onCancel, onDelete }) => {
  const [snoozeCount, setSnoozeCount] = useState(0);
  const [newTime, setNewTime] = useState(""); // State to handle user input for time

  const handleDelete = () => {
    onDelete(alarm.id);
  };

  const handlePlaySound = () => {
    const audio = new Audio("/path/to/sound.mp3");
    audio.play();
  };

  const handleRepeat = (e) => {
    onSave({ ...alarm, repeat: e.target.checked });
  };

  const handleSnooze = () => {
    if (snoozeCount < 3) {
      setSnoozeCount(snoozeCount + 1);
      const snoozeTime = alarm ? alarm.time + 5 * 60 : 0; // Check if alarm exists
      onSave({ ...alarm, time: snoozeTime });
    }
  };

  const handleChangeTime = (e) => {
    setNewTime(e.target.value);
  };

  const handleSaveAlarm = () => {
    // Example: Save new alarm with user input time
    onSave({
      id: Date.now(), // Example: Generate unique ID (replace with actual logic)
      time: parseInt(newTime, 10), // Example: Convert user input to integer (replace with validation)
      repeat: false, // Example: Default repeat value (modify as needed)
    });
    setNewTime(""); // Clear input after saving
  };

  // Ensure alarm is defined before accessing repeat property
  const repeatChecked = alarm && alarm.repeat;

  return (
    <div>
      <div>
        Time:
        <input
          type="number"
          value={newTime}
          onChange={handleChangeTime}
          placeholder="Enter time in seconds"
        />
        <button onClick={handleSaveAlarm}>Add Alarm</button>
      </div>
      <div>
        Repeat:
        <input
          type="checkbox"
          checked={repeatChecked}
          onChange={handleRepeat}
        />
      </div>
      <div>
        <button onClick={handleSnooze}>Snooze</button>
        <span>Snooze Count: {snoozeCount}</span>
      </div>
      <button onClick={handlePlaySound}>Play Sound</button>
      <button onClick={handleDelete}>Delete</button>
    </div>
  );
};

export default Alarm;
