import React, { useState, useEffect } from "react";
import "./Clock.css";

const Clock = () => {
  const [currentColor, setCurrentColor] = useState("#FFFFFF");
  const [time, setTime] = useState(new Date());

  // Update time every second
  useEffect(() => {
    const interval = setInterval(() => {
      setTime(new Date());
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  // Update color every second
  useEffect(() => {
    const interval = setInterval(() => {
      const randomColor =
        "#" + ((Math.random() * 0xffffff) << 0).toString(16).padStart(6, "0");
      setCurrentColor(randomColor);
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  const hours = ("0" + time.getHours()).slice(-2);
  const minutes = ("0" + time.getMinutes()).slice(-2);
  const seconds = ("0" + time.getSeconds()).slice(-2);
  const ampm = time.getHours() >= 12 ? "PM" : "AM";

  return (
    <div className="clock-container">
      <div className="time-display" style={{ color: currentColor }}>
        <div className="clock" style={{ backgroundColor: currentColor }}>
          <span className="time-part">{hours}</span>
          <span className="time-part">:</span>
          <span className="time-part">{minutes}</span>
          <span className="time-part">:</span>
          <span className="time-part">{seconds}</span>
        </div>
      </div>
      <div className="date-display">{time.toLocaleDateString()}</div>
      <div className="ampm-display">{ampm}</div>
    </div>
  );
};

export default Clock;
